sys_script_include.script:
var ApplicationText = Class.create();
ApplicationText.prototype = {
    initialize: function() {
    },


    type: 'ApplicationText'
};

ApplicationText.encodeArray = function(arr) {
	Expect.datatype.isArray(arr);
	return arr.join(',');
};

ApplicationText.decodeString = function(str) {
	Expect.datatype.isString(str);
	return str.split(',');
};
