sys_script_include.script:
var Logging = Class.create();
Logging.prototype = {
    initialize: function() {
    },

    type: 'Logging'
};

Logging._shouldLog = function() {
	return Metadata.getProperty('log.all') === 'true';
};

Logging._shouldLogVisible = function() {
	return Logging._shouldLog() && Metadata.getProperty('log.all.visible') === 'true';
};

Logging._shouldLogInfo = function() {
	return Logging._shouldLog() && Metadata.getProperty('log.info') === 'true';
};

Logging._shouldLogInfoVisible = function() {
	return Logging._shouldLogVisible() && Logging._shouldLogInfo() && Metadata.getProperty('log.info.visible') === true;
};

Logging._shouldLogWarn = function() {
	return Logging._shouldLog() && Metadata.getProperty('log.warn') === 'true';
};

Logging._shouldLogWarnVisible = function() {
	return Logging._shouldLogVisible() && Logging._shouldLogWarn() && Metadata.getProperty('log.warn.visible') === true;
};

Logging._shouldLogCritical = function() {
	return Logging._shouldLog() && Metadata.getProperty('log.critical') === true;
};

Logging._shouldLogCriticalVisible = function() {
	return Logging._shouldLogVisible() && Logging._shouldLogCritical() && Metadata.getProperty('log.critical.visible') === true;
};

Logging.info = function info(message) {
	Expect.type.isString(message);
	if(Logging._shouldLogInfo()) {
		gs.info(message);
	}

	if(Logging._shouldLogInfoVisible()) {
		gs.addInfoMessage(message);
	}
};

Logging.warn = function warn(message) {
	Expect.type.isString(message);
	if(Logging._shouldLogInfo()) {
		gs.warn(message);
	}

	if(Logging._shouldLogInfoVisible()) {
		gs.addErrorMessage(message);
	}
};

Logging.critical = function critical(message) {
	Expect.type.isString(message);
	if(Logging._shouldLogInfo()) {
		gs.error(message);
	}

	if(Logging._shouldLogInfoVisible()) {
		gs.addErrorMessage(message);
	}
};
